// parse payload (string or object)
let payload = msg.payload;
if (typeof payload === 'string') {
    try {
        payload = JSON.parse(payload);
    } catch (e) {
        node.warn('Invalid JSON payload: ' + e);
        return null;
    }
}
const topic = (msg.topic || '').toLowerCase();
const deviceTag = 'gateway';
const nowSec = Math.floor(Date.now()/1000);
const ts = (payload && payload.time) ? Number(payload.time) : nowSec;

// joystick
if (topic.endsWith('joystick')) {
    const x = Number(payload.x);
    const y = Number(payload.y);
    if (Number.isNaN(x) || Number.isNaN(y) || x < -1 || x > 1 || y < -1 || y > 1) {
        node.warn('Invalid joystick values: ' + JSON.stringify(payload));
        return null;
    }
    msg.payload = {
        measurement: 'telemetry',
        tags: { device: deviceTag, type: 'joystick' },
        fields: { joystick_x: x, joystick_y: y },
        timestamp: ts * 1e9
    };
    return msg;
}

// buttons
if (topic.endsWith('buttons')) {
    // single button
    if (payload.button !== undefined && payload.state !== undefined) {
        const button = String(payload.button).replace(/\s+/g,'_');
        const state = (typeof payload.state === 'boolean') ? (payload.state ? 1 : 0) : Number(payload.state);
        if (Number.isNaN(state)) { node.warn('Invalid button state'); return null; }
        const fieldKey = 'button_' + button;
        msg.payload = {
            measurement: 'telemetry',
            tags: { device: deviceTag, type: 'buttons' },
            fields: { [fieldKey]: state },
            timestamp: ts * 1e9
        };
        return msg;
    }
    // multi-button
    if (payload.buttons && typeof payload.buttons === 'object') {
        const fields = {};
        Object.keys(payload.buttons).forEach(k => {
            const val = Number(payload.buttons[k]);
            if (!Number.isNaN(val)) fields['button_' + String(k).replace(/\s+/g,'_')] = val;
        });
        if (Object.keys(fields).length === 0) return null;
        msg.payload = {
            measurement: 'telemetry',
            tags: { device: deviceTag, type: 'buttons' },
            fields: fields,
            timestamp: ts * 1e9
        };
        return msg;
    }
    node.warn('Unrecognized buttons payload: ' + JSON.stringify(payload));
    return null;
}

node.warn('Unknown topic: ' + topic);
return null;
